import {Link} from "react-router-dom";
import {useContext, useEffect, } from "react";
import { UserContext } from "./components/UserContext";
import search_icon from "./assets/search-b.png";
export default function Header() {
  
  // SEARCH
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    if (!searchQuery) return;

    try {
      const response = await fetch(`http://localhost:4050/search?query=${searchQuery}`);
      const results = await response.json();
      setSearchResults(results);
    } catch (err) {
      console.error("Search error:", err);
    }
  };




  const {setUserInfo,userInfo} = useContext(UserContext);
  useEffect(() => {
      fetch('http://localhost:4050/profile', {
      method: 'GET',
      credentials: 'include',
    }).then(response => {
        response.json().then(userInfo => {
            setUserInfo(userInfo);
          });
        });
      }, [setUserInfo]);
      
      function logout() {
          fetch('http://localhost:4050/logout', {
              credentials: 'include',
              method: 'POST',
            });
            setUserInfo(null);
          }
          
          const username = userInfo?.username;
          const role = userInfo?.role;
          return (
              <header className="container">
                <Link to="/" className="logo">AnalyzedByIITIANS</Link>
              <nav>
                <Link to="/">Home</Link>
                  {username && (
          <>
            {role === 'ADMIN' &&
              <Link to="/create">
                Add new course
              </Link>}
            <a href="/" onClick={logout}>Logout ({username})</a>
          </>
        )}
        {!username && (
            <>
              <Link to="/login">Login</Link>
              <Link to="/register">Register</Link>
            </>
                )}
                <div className="search-box">
  <input className="search-bar" placeholder="Search..." />
  <a href="/search"><img src={search_icon} alt="Search Icon" className="search-icon" /></a>

</div>

                
       </nav>  
      </header>
    );
}
