import React, { useState } from "react";
import Editor from "../components/Editor"; // Assuming Editor is a rich text editor component

export default function CreatePost() {
  const [title, setTitle] = useState("");
  const [courseOwner, setCourseOwner] = useState("");
  const [summary, setSummary] = useState("");
  const [tags, setTags] = useState("");
  const [difficulty, setDifficulty] = useState("Beginner");
  const [date, setDate] = useState("");
  const [files, setFiles] = useState(null);
  const [content, setContent] = useState("");

  const handleFileChange = (ev) => {
    const fileList = ev.target.files;
    setFiles(fileList ? Array.from(fileList) : []);
  };

  const handleSubmit = (ev) => {
    ev.preventDefault();
    // Submit logic here
    console.log({
      title,
      courseOwner,
      summary,
      tags: tags.split(",").map(tag => tag.trim()),
      difficulty,
      date,
      files,
      content,
    });
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      <h2>Add Course</h2>

      {/* Title Input */}
      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={(ev) => setTitle(ev.target.value)}
        required
      />

      {/* Course Owner Input */}
      <input
        type="text"
        placeholder="Course Owner"
        value={courseOwner}
        onChange={(ev) => setCourseOwner(ev.target.value)}
        required
      />

      {/* Summary Input */}
      <textarea
        placeholder="Summary"
        value={summary}
        onChange={(ev) => setSummary(ev.target.value)}
        rows="3"
      ></textarea>

      {/* Tags Input */}
      <input
        type="text"
        placeholder="Tags (comma-separated)"
        value={tags}
        onChange={(ev) => setTags(ev.target.value)}
      />

      {/* Difficulty Dropdown */}
      <select
        value={difficulty}
        onChange={(ev) => setDifficulty(ev.target.value)}
      >
        <option value="Beginner">Beginner</option>
        <option value="Intermediate">Intermediate</option>
        <option value="Advanced">Advanced</option>
      </select>

      {/* Date Picker */}
      <input
        type="date"
        value={date}
        onChange={(ev) => setDate(ev.target.value)}
      />

      {/* File Upload */}
      <input
        type="file"
        onChange={handleFileChange}
        multiple
      />

      {/* Preview Uploaded Files */}
      {files && files.length > 0 && (
        <div className="file-preview">
          <h4>Uploaded Files:</h4>
          <ul>
            {files.map((file, index) => (
              <li key={index}>{file.name}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Content Editor */}
      <Editor value={content} onChange={setContent} />

      {/* Submit Button */}
      <button type="submit" style={{ marginTop: "10px" }}>
        Add Course
      </button>
    </form>
  );
}
