import { format } from 'date-fns';
import { Link } from "react-router-dom";

export default function Post({ _id, title, courseOwner, summary, cover, createdAt, logofile }) {
  let formattedDate = "Unknown date";
  
  // Ensure createdAt is valid before formatting
  if (createdAt) {
    const dateObj = new Date(createdAt);
    if (!isNaN(dateObj.getTime())) {
      formattedDate = format(dateObj, 'yyyy-MM-dd HH:mm:ss'); // Format as needed
    }
  }

  return (
    <div className="post">
      {cover && (
        <div className="image">
          <Link to={`/post/${_id}`}>
            <img
              src={`http://localhost:4050/${cover}`}
              alt={title || "Post cover"}
            />
          </Link>
        </div>
      )}
      
      <div className="texts">
        <Link to={`/post/${_id}`}>
          <h2>{title}</h2>
        </Link>
        <p className="info">
          <Link to={`/post/${_id}`} className="logofile">
            <img  
              src={`http://localhost:4050/${logofile}`} 
              alt={title || "logofile"} 
              className="logofile-img"
            />
          </Link>

          <span className="postcourseOwner">{courseOwner}</span>
        </p>

        <p className="summary">{summary}</p>
        <time className='postTime'>{formattedDate}</time>
      </div>
    </div>
  );
}
