import { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

export default function SearchHandlingPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Use useLocation to get the query parameters from the URL
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const query = queryParams.get('query');

  useEffect(() => {
    if (query) {
      setSearchQuery(query);
      handleSearch(query);
    }
  }, [query]);

  const handleSearch = async (query) => {
    if (!query) return;

    setLoading(true);
    setError('');
    setSearchResults([]);

    try {
      const response = await fetch(`http://localhost:4050/search?query=${query}`);
      if (!response.ok) {
        throw new Error('Failed to fetch search results');
      }
      const results = await response.json();
      setSearchResults(results);
    } catch (err) {
      console.error('Search error:', err);
      setError('Something went wrong. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <header className="contain">
      <nav>
        {/* Display Loading State */}
        {loading && <p>Loading...</p>}

        {/* Display Error Message */}
        {error && <p className="error-message">{error}</p>}

        {/* Display Search Results */}
        <div className="search-results">
          {searchResults.length > 0 ? (
            searchResults.map((post) => (
              <div key={post._id} className="search-item">
                <h3>{post.title}</h3>
                <p>{post.summary}</p>
              </div>
            ))
          ) : (
            !loading && !error && <p>No results found</p>
          )}
        </div>
      </nav>
    </header>
  );
}
