import { format } from 'date-fns';
import { Link } from "react-router-dom";

export default function Post({ _id, title, courseOwner, summary, cover, createdAt, logofile }) {
  // const authorName = author?.username || "Anonymous"; 

  return (
    <div className="post">
      {/* Image Section */}
      {cover && (
        <div className="image">
          <Link to={`/post/${_id}`}>
            <img
              src={`http://localhost:4050/${cover}`}
              alt={title || "Post cover"}
            />
          </Link>
        </div>
      )}
      <div className='combine'>

        <Link to={`/post/${_id}`}>
          <h2>{title}</h2>
        </Link>

      <div className="texts">
        <p className="info">
        <Link to={`/post/${_id}`} classname='logofile' >
            <img  
              src={`http://localhost:4050/${logofile}`}
              alt={title || "logofile"}
               className="logofile-img"
            />
          </Link>
          <span className="author">{courseOwner}</span>
          <time>{createdAt ? format(new Date(createdAt), 'yyyy-MM-dd')  : "Unknown date"}</time>
        </p>

        <p className="summary">{summary}</p>
      </div>
      </div>
    </div>
  );
}
