import { useContext, useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { formatISO9075 } from "date-fns";
import { UserContext } from "../components/UserContext";
import { Link } from 'react-router-dom';

export default function PostPage() {
  const [postInfo, setPostInfo] = useState(null);
  const { userInfo } = useContext(UserContext);
  const { id } = useParams();
  const navigate = useNavigate();
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:4050/post/${id}`)
      .then(response => {
        response.json().then(postInfo => {
          setPostInfo(postInfo);
          setComments(postInfo.comments || []);
        });
      });
  }, [id]);

  async function deletePost() {
    const confirmDelete = window.confirm("Are you sure you want to delete this post?");
    if (!confirmDelete) return;

    const response = await fetch(`http://localhost:4050/post/${id}`, {
      method: "DELETE",
      credentials: "include",
    });

    if (response.ok) {
      alert("Post deleted successfully!");
      navigate("/");
    } else {
      alert("Failed to delete the post.");
    }
  }

  async function submitForm() {
    if (rating === 0 || comment.trim() === "") {
      alert("Please provide both a rating and a comment.");
      return;
    }

    const response = await fetch(`http://localhost:4050/post/${id}/comment-and-rate`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        userId: userInfo.id,
        username: userInfo.username,
        text: comment,
        rating,
      }),
    });

    if (response.ok) {
      const updatedPost = await response.json();
      setComments(updatedPost.comments);
      setComment('');
      setRating(0);
      alert("Your comment and rating have been submitted!");
    } else {
      alert("Failed to submit your comment and rating.");
    }
  }

  // Delete comment
  async function deleteComment(commentId) {
    const response = await fetch(`http://localhost:4050/post/${id}/comment/${commentId}`, {
      method: "DELETE",
      credentials: "include",
    });

    if (response.ok) {
      const updatedPost = await response.json();
      setComments(updatedPost.comments);
      alert("Comment deleted successfully!");
    } else {
      alert("Failed to delete comment.");
    }
  }

  // Edit comment
  async function editComment(commentId, newText) {
    const response = await fetch(`http://localhost:4050/post/${id}/comment/${commentId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        text: newText,
      }),
    });

    if (response.ok) {
      const updatedPost = await response.json();
      setComments(updatedPost.comments);
      alert("Comment edited successfully!");
    } else {
      alert("Failed to edit comment.");
    }
  }

  if (!postInfo) return '';

return (
  <div className="post-page">
    <h1>{postInfo.title}</h1>
    <time>{formatISO9075(new Date(postInfo.createdAt))}</time>
    <p className="courseOwner">{postInfo.courseOwner}</p>

    {userInfo.id === postInfo.author._id && (
      <div className="edit-row">
        <div className="btn-container">
          <Link className="edit-btn" to={`/edit/${postInfo._id}`}>
            Edit Course Info
          </Link>
          <Link className="edit-btn delete-btn" onClick={deletePost}>
            Remove Course
          </Link>
        </div>
      </div>
    )}

    <div className="image">
      <img src={`http://localhost:4050/${postInfo.cover}`} alt="" />
    </div>
    <div className="content" dangerouslySetInnerHTML={{ __html: postInfo.content }} />
    <div className="author">by @{postInfo.author.username}</div>

    {/* Wrap the rating and comment section inside a parent div or fragment */}
    <div className="rating-comment-section">
      <h3>Rate and Comment:</h3>

      {userInfo.username && (
        <>
          <div className="rating">
            {[1, 2, 3, 4, 5].map((star) => (
              <span
                key={star}
                className={`star ${star <= rating ? "filled" : ""}`}
                onClick={() => setRating(star)}
              >
                ★
              </span>
            ))}
          </div>
          <div className="comment-form">
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Add your comment..."
            />
          </div>
          <button onClick={submitForm}>Submit Comment and Rating</button>
        </>
      )}
    </div>

    <div className="comments-list">
      <h3>Comments:</h3>
      {comments.map((c, index) => (
        <div key={index} className="comment">
          <p><strong>{c.username}</strong>: {c.text}</p>
          <p>Rating: {[...Array(c.rating)].map((_, i) => "★").join("")}</p>

          {userInfo.id === c.userId && (
            <div className="comment-actions">
              <button onClick={() => editComment(c._id, prompt("Edit your comment", c.text))}>
                Edit
              </button>
              <button onClick={() => deleteComment(c._id)}>Delete</button>
            </div>
          )}
        </div>
      ))}
    </div>
  </div>
);

}
