import { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { UserContext } from "../components/UserContext";

export default function CommentPage() {
  const { id } = useParams(); // Post ID from the URL
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const { userInfo } = useContext(UserContext);

  useEffect(() => {
    // Fetch comments for the specific post
    fetch(`http://localhost:4050/post/${id}/comments`)
      .then((response) => response.json())
      .then((data) => setComments(data));
  }, [id]);

  async function addComment() {
    if (!newComment.trim()) {
      alert("Comment cannot be empty.");
      return;
    }

    const response = await fetch(`http://localhost:4050/post/${id}/comments`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "include",
      body: JSON.stringify({ text: newComment }),
    });

    if (response.ok) {
      const addedComment = await response.json();
      setComments([...comments, addedComment]); // Add the new comment to the state
      setNewComment(""); // Clear the input field
    } else {
      alert("Failed to add comment.");
    }
  }

  async function deleteComment(commentId) {
    const confirmDelete = window.confirm("Are you sure you want to delete this comment?");
    if (!confirmDelete) return;

    const response = await fetch(`http://localhost:4050/post/${id}/comments/${commentId}`, {
      method: "DELETE",
      credentials: "include",
    });

    if (response.ok) {
      setComments(comments.filter((comment) => comment._id !== commentId)); // Remove the deleted comment
    } else {
      alert("Failed to delete comment.");
    }
  }

  async function addRating(commentId, rating) {
    const response = await fetch(`http://localhost:4050/post/${id}/comments/${commentId}/rate`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "include",
      body: JSON.stringify({ rating }),
    });

    if (response.ok) {
      const updatedComment = await response.json();
      setComments(comments.map((comment) => (comment._id === commentId ? updatedComment : comment))); // Update the rated comment
    } else {
      alert("Failed to rate the comment.");
    }
  }

  return (
    <div className="comment-section">
      <h2>Comments</h2>
      <div className="comment-input">
        <textarea
          placeholder="Write a comment..."
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        ></textarea>
        <button onClick={addComment}>Add Comment</button>
      </div>
      <div className="comments-list">
        {comments.map((comment) => (
          <div key={comment._id} className="comment-item">
            <div className="comment-header">
              <strong>@{comment.author.username}</strong>{" "}
              <small>{new Date(comment.createdAt).toLocaleString()}</small>
            </div>
            <p>{comment.text}</p>
            <div className="rating">
              <span>Rate: </span>
              {[1, 2, 3, 4, 5].map((star) => (
                <button
                  key={star}
                  onClick={() => addRating(comment._id, star)}
                  className={star <= (comment.rating || 0) ? "active-star" : ""}
                >
                  â˜…
                </button>
              ))}
              <span>({comment.rating || 0})</span>
            </div>
            {userInfo.id === comment.author._id && (
              <button className="delete-btn" onClick={() => deleteComment(comment._id)}>
                Delete
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
