import { formatISO9075 } from "date-fns";
import { Link } from "react-router-dom";

export default function Post({ _id, title, summary, cover, createdAt, author }) {
  const authorName = author?.username || "Anonymous"; 

  return (
    <div className="post">
      {/* Image Section */}
      {cover && (
        <div className="image">
          <Link to={`/post/${_id}`}>
            <img
              src={`http://localhost:4050/${cover}`}
              alt={title || "Post cover"}
            />
          </Link>
        </div>
      )}

      {/* Text Section */}
      <div className="texts">
        {/* Title */}
        <Link to={`/post/${_id}`}>
          <h2>{title}</h2>
        </Link>

        {/* Author and Date */}
        <p className="info">
          <span className="author">{authorName}</span>
          <time>{createdAt ? formatISO9075(new Date(createdAt)) : "Unknown date"}</time>
        </p>

        {/* Summary */}
        <p className="summary">{summary}</p>
      </div>
    </div>
  );
}
