import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Register() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");

  // Function to validate strong password
  const isStrongPassword = (password) => {
    const strongPasswordRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return strongPasswordRegex.test(password);
  };

  const isStrongUsername = (username) => {
    const strongUsernameRegex = /^[A-Za-z0-9]{3,}$/; // No spaces, alphanumeric, 3+ characters
    return strongUsernameRegex.test(username);
  };

  const navigate = useNavigate(); // Move this outside of the handleSubmit function

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isStrongUsername(username)) {
      setError("Username must be at least 3 characters long, contain only alphanumeric characters, and cannot have spaces.");
      return;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match!");
      return;
    }

    if (!isStrongPassword(password)) {
      setError(
        "Password must be at least 8 characters long, contain an uppercase letter, a lowercase letter, a number, and a special character."
      );
      return;
    }

    try {
      const response = await fetch("http://localhost:4050/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, email, password }),
      });

      if (response.ok) {
        alert("Registration successful!");
        navigate("/login"); // Redirect after successful registration
      } else {
        const data = await response.json();
        setError(data.error || "Registration failed!");
      }
    } catch (err) {
      setError("An error occurred during registration.");
    }
  };

  return (
    <form className="register" onSubmit={handleSubmit}>
      <h1>Register</h1>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Confirm Password"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
        required
      />
      <button type="submit">Register</button>
      <p className="link">
        <Link to="/login">I have an account?</Link>
      </p>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </form>
  );
}
