import { useContext, useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { formatISO9075 } from "date-fns";
import { UserContext } from "../components/UserContext";
import { Link } from 'react-router-dom';

export default function PostPage() {
  const [postInfo, setPostInfo] = useState(null);
  const { userInfo } = useContext(UserContext);
  const { id } = useParams();
  const navigate = useNavigate();
  const [rating, setRating] = useState(0); // State for storing the rating
  const [comment, setComment] = useState(''); // State for storing the comment
  const [comments, setComments] = useState([]); // State for storing the list of comments

  useEffect(() => {
    fetch(`http://localhost:4050/post/${id}`)
      .then(response => {
        response.json().then(postInfo => {
          setPostInfo(postInfo);
          setComments(postInfo.comments || []); // Initialize comments if they exist
        });
      });
  }, [id]);

  async function deletePost() {
    const confirmDelete = window.confirm("Are you sure you want to delete this post?");
    if (!confirmDelete) return;

    const response = await fetch(`http://localhost:4050/post/${id}`, {
      method: "DELETE",
      credentials: "include",
    });

    if (response.ok) {
      alert("Post deleted successfully!");
      navigate("/"); 
    } else {
      alert("Failed to delete the post.");
    }
  }

  // Handle comment submission
  async function submitComment() {
    const response = await fetch(`http://localhost:4050/post/${id}/comment`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        userId: userInfo.id,
        username: userInfo.username,
        text: comment,
      }),
    });

    if (response.ok) {
      const updatedPost = await response.json();
      setComments(updatedPost.comments); // Update comments
      setComment(''); // Reset the comment input
    } else {
      alert("Failed to add the comment.");
    }
  }

  // Handle rating submission
  async function submitRating() {
    const response = await fetch(`http://localhost:4050/post/${id}/rate`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ rating }),
    });

    if (response.ok) {
      alert("Rating submitted!");
    } else {
      alert("Failed to submit rating.");
    }
  }

  if (!postInfo) return '';

  return (
    <div className="post-page">
      <h1>{postInfo.title}</h1>
      <time>{formatISO9075(new Date(postInfo.createdAt))}</time>
      <p className="courseOwner">{postInfo.courseOwner}</p>

      {userInfo.id === postInfo.author._id && (
        <div className="edit-row">
          <div className="btn-container">
            <Link className="edit-btn" to={`/edit/${postInfo._id}`}>
              Edit Course Info
            </Link>
            <Link className="edit-btn delete-btn" onClick={deletePost}>
              Remove Course
            </Link>
          </div>
        </div>
      )}

      <div className="image">
        <img src={`http://localhost:4050/${postInfo.cover}`} alt="" />
      </div>
      <div className="content" dangerouslySetInnerHTML={{ __html: postInfo.content }} />
      <div className="author">by @{postInfo.author.username}</div>

      {/* Rating Section */}
      <div className="rating-section">
        <h3>Rate this Course:</h3>
        <div className="rating">
          {[1, 2, 3, 4, 5].map(star => (
            <span
              key={star}
              className={`star ${star <= rating ? "filled" : ""}`}
              onClick={() => setRating(star)}
            >
              ★
            </span>
          ))}
        </div>
        <button onClick={submitRating}>Submit Rating</button>
      </div>

      {/* Comment Section */}
      <div className="comment-section">
        <h3>Comments:</h3>
        <div className="comment-form">
          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Add your comment..."
          />
          <button onClick={submitComment}>Submit Comment</button>
        </div>
        <div className="comments-list">
          {comments.map((c, index) => (
            <div key={index} className="comment">
              <p><strong>{c.username}</strong>: {c.text}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
