import React, { useRef } from "react";
import ReactQuill, { Quill } from "react-quill";
import "react-quill/dist/quill.snow.css";

// Add image resize module
import ImageResize from "quill-image-resize-module";
Quill.register("modules/imageResize", ImageResize);

export default function Editor({ value, onChange }) {
  const quillRef = useRef(null);

  // Custom image handler for image uploads
  const handleImageUpload = () => {
    const input = document.createElement("input");
    input.setAttribute("type", "file");
    input.setAttribute("accept", "image/*");
    input.click();

    input.onchange = async () => {
      const file = input.files[0];
      const formData = new FormData();
      formData.append("image", file);

      // Simulate API call to upload image
      const response = await fetch("https://api.imgbb.com/1/upload?key=YOUR_API_KEY", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      const imageUrl = data.data.url;

      // Insert uploaded image into the editor
      const quill = quillRef.current.getEditor();
      const range = quill.getSelection();
      quill.insertEmbed(range.index, "image", imageUrl);
    };
  };

  // Quill modules configuration
  const modules = {
    toolbar: {
      container: [
        [{ header: [1, 2, false] }], // Headings
        ["bold", "italic", "underline", "strike", "blockquote"], // Formatting
        [
          { list: "ordered" },
          { list: "bullet" },
          { indent: "-1" },
          { indent: "+1" },
        ], // Lists and indents
        ["link", "image"], // Insert options
        ["clean"], // Clear formatting
      ],
      handlers: {
        image: handleImageUpload, // Custom image handler
      },
    },
    imageResize: {
      modules: ["Resize", "DisplaySize", "Toolbar"], // Image resize functionality
    },
  };

  // Quill formats configuration
  const formats = [
    "header",
    "bold",
    "italic",
    "underline",
    "strike",
    "blockquote",
    "list",
    "bullet",
    "indent",
    "link",
    "image",
  ];

  return (
    <div className="content">
      <ReactQuill
        ref={quillRef}
        value={value}
        onChange={onChange}
        theme="snow"
        modules={modules}
        formats={formats}
        placeholder="Write something amazing..."
      />
    </div>
  );
}
