import { Link } from "react-router-dom";
import { useContext, useEffect, useState } from "react";
import { UserContext } from "./components/UserContext";
// import search_icon from "./assets/search-b.png";


import logo_light from '../../assets/logo-black.png';
import logo_dark from '../../assets/logo-white.png';
import search_icon from '../../assets/search-b.png';
// import search_icon_dark from '../../assets/search-b.png';
import toggle_light from '../../assets/night.png';
import toggle_dark from '../../assets/day.png';


export default function Header() {
  const { setUserInfo, userInfo } = useContext(UserContext);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    fetch("http://localhost:4050/profile", {
      method: "GET",
      credentials: "include",
    }).then((response) => {
      response.json().then((userInfo) => {
        setUserInfo(userInfo);
      });
    });
  }, [setUserInfo]);

  function logout() {
    fetch("http://localhost:4050/logout", {
      credentials: "include",
      method: "POST",
    });
    setUserInfo(null);
  }

  const username = userInfo?.username;
  const role = userInfo?.role;

  const [theme, setTheme] = useState("light"); // Manage theme state

 const toggle_mode = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  return (
<header className={`navbar ${theme}`}>
      {/* Logo */}
      <Link to="/" className="logo">
        <img src={theme === 'light' ? logo_light : logo_dark} alt="Logo" />
      </Link>

      {/* Navigation Links */}
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          {username && (
            <>
              {role === "ADMIN" && (
                <li><Link to="/create">Add new course</Link></li>
              )}
              <li><a href="/" onClick={logout}>Logout ({username})</a></li>
            </>
          )}

          {!username && (
            <>
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/register">Register</Link></li>
            </>
          )}
        </ul>
      </nav>

      {/* Search Box */}
      <div className="search-box">
        <input
          className="search-bar"
          placeholder="Search..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        {searchQuery ? (
          <Link to={`/search?query=${searchQuery}`}>
            <img src={search_icon} alt="Search Icon" className="search-icon" />
          </Link>
        ) : (
          <img src={search_icon} alt="Search Icon" className="search-icon" />
        )}
      </div>

      {/* Theme Toggle Icon */}
      <img
        src={theme === "light" ? toggle_light : toggle_dark}
        alt="Toggle Theme"
        className="toggle-icon"
        onClick={toggle_mode}
      />
    </header>

  );
}
