import React, { useState, useEffect } from 'react';
import Footer from './Footer';
import Header from './Header';
import { Outlet, useLocation } from 'react-router-dom';

export default function Layout() {
  const [showFooter, setShowFooter] = useState(false);
  const location = useLocation(); 

  // Show footer after 3 seconds, but only on certain routes
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowFooter(true); 
    }, 3000); 
    
    return () => clearTimeout(timer);
  }, []); 

  // List of routes where footer should not be displayed
  const hideFooterRoutes = ['/edit', '/login', '/create', '/register', '/search'];

  const shouldShowFooter = !hideFooterRoutes.some(route => location.pathname.startsWith(route));

  return (
    <main className="layout-container">
      <Header />
      <div className="content">
        <Outlet />
      </div>
      {showFooter && shouldShowFooter && <Footer />} 
    </main>
  );
}
