import { format } from "date-fns";
import { Link, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

export default function SearchHandlingPage() {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const query = queryParams.get("query");

  useEffect(() => {
    if (query) {
      handleSearch(query);
    }
  }, [query]);

  const handleSearch = async (query) => {
    if (!query) return;

    setLoading(true);
    setError("");
    setSearchResults([]);

    try {
      const response = await fetch(`http://localhost:4050/search?query=${query}`);
      if (!response.ok) {
        throw new Error("Failed to fetch search results");
      }
      const results = await response.json();
      setSearchResults(results);
    } catch (err) {
      console.error("Search error:", err);
      setError("Something went wrong. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1 className="searchResult">Search Results</h1>

      {loading && <h2 className="searchResult loading">Loading...</h2>}
      {error && <p className="error">{error}</p>}

      <div className="search-results">
        {searchResults.length > 0 ? (
          searchResults.map((post) => (
            <Post key={post._id} {...post} />
          ))
        ) : (
          !loading &&
          !error && (
            <h2 className="searchResult no-results">
              No results found. Please try a different search.
            </h2>
          )
        )}
      </div>
    </div>
  );
}

function Post({ _id, title, cover, logofile, courseOwner, summary, createdAt }) {
  return (
    <div className="post">
      {/* Cover Image */}
      {cover && (
        <div className="image">
          <Link to={`/post/${_id}`}>
            <img
              src={`http://localhost:4050/${cover}`}
              alt={title || "Post cover"}
              onError={(e) => (e.target.style.display = "none")} // Hide if broken
            />
          </Link>
        </div>
      )}

      {/* Post Text Content */}
      <div className="texts">
        {/* Title */}
        <Link to={`/post/${_id}`}>
          <h2>{title}</h2>
        </Link>

        {/* Info Section (Logo, Course Owner, Date) */}
        <p className="info">
          {/* Logo File */}
          {logofile && (
            <Link to={`/post/${_id}`} className="logofile">
              <img
                src={`http://localhost:4050/${logofile.replace(/\\/g, "/")}`}
                alt={title || "logofile"}
                className="logofile-img"
                onError={(e) => (e.target.style.display = "none")}
              />
            </Link>
          )}

          {/* Course Owner */}
          <span className="postcourseOwner">{courseOwner}</span>
        </p>

        {/* Summary */}
        <p className="summary">{summary}</p>

        {/* Post Date */}
        <time className="postTime">
          {createdAt ? format(new Date(createdAt), "yyyy-MM-dd") : "Unknown date"}
        </time>
      </div>
    </div>
  );
}
