import { useContext, useState, useEffect } from "react";
import { Navigate } from "react-router-dom";
import { UserContext } from "../components/UserContext";
import { Link } from "react-router-dom";

export default function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [redirect, setRedirect] = useState(false);
  const [error, setError] = useState('');
  const { setUserInfo } = useContext(UserContext);

  useEffect(() => {
    // Reset error state on input change
    setError('');
  }, [username, password]);

  async function login(ev) {
    ev.preventDefault();
    
    // Check if username has spaces
    if (/\s/.test(username)) {
      setError('Username cannot contain spaces');
      return;
    } else {
      setError(''); // Clear error if no spaces
    }

    // Request to authenticate user
    const response = await fetch('http://localhost:4050/login', {
      method: 'POST',
      body: JSON.stringify({ username, password }),
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
    });

    if (response.ok) {
      const userInfo = await response.json();
      setUserInfo(userInfo);  // Store the user information in context
      setRedirect(true);  // Set the flag to redirect to home page
    } else {
      setError('Wrong credentials, please try again.');  // Show error message in UI
    }
  }

  if (redirect) {
    return <Navigate to="/" />;  // Redirect to home page after login
  }

  return (
    <form className="login" onSubmit={login}>
      <h1>Login</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}  {/* Display error message */}
      <input
        type="text"
        placeholder="Username or Email"
        value={username}
        onChange={ev => setUsername(ev.target.value.trim())}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={ev => setPassword(ev.target.value)}
      />
      <button type="submit">Login</button>
      <p className="link">
        <div className="forgot">
          <Link to="/forgot-password">Forgot Password?</Link>
        </div>
        <Link to="/register">Don't have an account?</Link>
      </p>
    </form>
  );
}
