import { formatISO9075 } from "date-fns";
import { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

export default function SearchHandlingPage() {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Get query parameter from URL
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const query = queryParams.get("query");

  useEffect(() => {
    if (query) {
      handleSearch(query);
    }
  }, [query]);

  const handleSearch = async (query) => {
    if (!query) return;

    setLoading(true);
    setError("");
    setSearchResults([]);

    try {
      const response = await fetch(`http://localhost:4050/search?query=${query}`);
      if (!response.ok) {
        throw new Error("Failed to fetch search results");
      }
      const results = await response.json();
      setSearchResults(results);
    } catch (err) {
      console.error("Search error:", err);
      setError("Something went wrong. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Search Results</h1>

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}

      <div className="search-results">
  {searchResults.map((post) => (
    <div className="post" key={post._id}>
      {/* Image Section */}
      {post.cover && (
        <div className="image">
          <Link to={`/post/${post._id}`}>
            <img
              src={`http://localhost:4050/${post.cover}`}
              alt={post.title || "Post cover"}
            />
          </Link>
        </div>
      )}

      {/* Text Section */}
      <div className="texts">
        {/* Title */}
        <Link to={`/post/${post._id}`}>
          <h2>{post.title}</h2>
        </Link>

        {/* Author and Date */}
        <p className="info">
          <span className="author">{post.author.username}</span>
          <time>
            {post.createdAt ? formatISO9075(new Date(post.createdAt)) : "Unknown date"}
          </time>
        </p>

        {/* Summary */}
        <p className="summary">{post.summary}</p>
      </div>
    </div>
  ))}
</div>

    </div>
  );
}
