import React, { useRef } from "react";
import ReactQuill from "react-quill";
import { Quill } from "react-quill";
import "react-quill/dist/quill.snow.css"; // Import Quill styles

// Import image resize module
import ImageResize from "quill-image-resize-module-react"; // Import the correct package for React

// Register the image resize module with Quill
Quill.register("modules/imageResize", ImageResize);

export default function Editor({ value, onChange }) {
  const quillRef = useRef(null);

  // Define the formats that can be applied in the editor
  const formats = [
    'header', 'bold', 'italic', 'underline', 'strike', 'blockquote', 'list', 'bullet', 
    'indent', 'link', 'image', 'align', 'font', 'size', 'color', 'background', 'script'
  ];

  // Define the toolbar options with additional formatting options
  const modules = {
    toolbar: [
      [{ header: [1, 2, false] }], // Header options
      ['bold', 'italic', 'underline', 'strike'], // Text styles
      ['blockquote'], // Blockquote option
      [{ list: 'ordered' }, { list: 'bullet' }], // List styles
      [{ indent: '-1' }, { indent: '+1' }], // Indentation
      ['link', 'image'], // Link and image options
      [{ align: [] }], // Text alignment options
      [{ font: [] }], // Font family options
      [{ size: ['small', 'medium', 'large', 'huge'] }], // Text size options
      [{ color: [] }, { background: [] }], // Text color and background color options
      [{ script: 'sub' }, { script: 'super' }], // Subscript and superscript options
      ['clean'], // Clear formatting
    ],
    imageResize: {
      modules: ['Resize', 'DisplaySize', 'Toolbar'],
    },
  };

  return (
    <div className="content">
      <ReactQuill
        ref={quillRef}
        value={value}
        onChange={onChange}
        theme="snow"
        modules={modules}
        formats={formats} // Ensure only the specified formats are available
        placeholder="Write something amazing..."
      />
    </div>
  );
}
