import React, { useContext, useEffect } from "react";
import "./Navbar.css";
import logoLight from "../../image/logo-black.png";
import logoDark from "../../image/logo-white.png";
import searchIconLight from "../../image/search-w.png";
import searchIconDark from "../../image/search-b.png";
import toggleLight from "../../image/night.png";
import toggleDark from "../../image/day.png";
import { Link } from "react-router-dom";
import { UserContext } from "../UserContext";

export default function Navbar({ theme, setTheme }) {
  const { setUserInfo, userInfo } = useContext(UserContext);

  useEffect(() => {
    fetch("http://localhost:4050/profile", {
      method: "GET",
      credentials: "include",
    })
      .then((response) => response.json())
      .then((userInfo) => {
        setUserInfo(userInfo);
      });
  }, [setUserInfo]);

  const logout = () => {
    fetch("http://localhost:4050/logout", {
      credentials: "include",
      method: "POST",
    });
    setUserInfo(null);
  };

  const username = userInfo?.username;
  const role = userInfo?.role;

  const toggleMode = () => {
    setTheme((prev) => (prev === "light" ? "dark" : "light"));
  };

  return (
    <div className={`navbar ${theme}`}>
      <img
        src={theme === "light" ? logoLight : logoDark}
        alt="Site Logo"
        className="logo"
      />
      <nav>
        <Link to="/">Home</Link>
        {username && (
          <>
            {role === "ADMIN" && <Link to="/create">Add New Course</Link>}
            <a href="/" onClick={logout}>
              Logout ({username})
            </a>
          </>
        )}
        {!username && <Link to="/login">Login</Link>}
      </nav>
      <div className="search-box">
        <input type="text" placeholder="Search" />
        <img
          src={theme === "light" ? searchIconLight : searchIconDark}
          alt="Search Icon"
        />
      </div>
      <img
        src={theme === "light" ? toggleLight : toggleDark}
        alt="Toggle Theme"
        className="toggle-icon"
        onClick={toggleMode}
      />
    </div>
  );
}
