import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function ProfilePage() {
  const [userInfo, setUserInfo] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [updatedInfo, setUpdatedInfo] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user information from the backend
    fetch("http://localhost:4050/user/profile", {
      method: "GET",
      credentials: "include",
    })
      .then((response) => response.json())
      .then((data) => {
        setUserInfo(data);
        setUpdatedInfo(data); // Initialize updatedInfo with the user's current info
      })
      .catch((error) => console.error("Error fetching user data:", error));
  }, []);

  const handleEditToggle = () => {
    setEditMode(!editMode);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedInfo({ ...updatedInfo, [name]: value });
  };

  const handleSaveChanges = async () => {
  if (!updatedInfo.email.includes("@")) {
    alert("Please enter a valid email address.");
    return;
  }
  
  const response = await fetch("http://localhost:4050/user/profile", {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(updatedInfo),
    credentials: "include",
  });

  if (response.ok) {
    alert("Profile updated successfully!");
    setUserInfo(updatedInfo);
    setEditMode(false);
  } else {
    alert("Failed to update profile.");
  }
};


  const handleLogout = async () => {
    await fetch("http://localhost:4050/logout", {
      method: "POST",
      credentials: "include",
    });
    navigate("/login");
  };

  if (!userInfo)
  return (
    <div className="flex justify-center items-center h-screen">
      <div className="spinner-border animate-spin inline-block w-8 h-8 border-4 rounded-full"></div>
    </div>
  );


  return (
    <div className="profile-page p-4">
      <h1 className="text-2xl font-bold mb-4">Profile Page</h1>

      <div className="profile-info mb-4">
        <div className="profile-field">
          <label className="block font-semibold">Username:</label>
          {editMode ? (
            <input
              type="text"
              name="username"
              value={updatedInfo.username}
              onChange={handleInputChange}
              className="border rounded p-2 w-full"
            />
          ) : (
            <p>{userInfo.username}</p>
          )}
        </div>

        <div className="profile-field">
          <label className="block font-semibold">Email:</label>
          {editMode ? (
            <input
              type="email"
              name="email"
              value={updatedInfo.email}
              onChange={handleInputChange}
              className="border rounded p-2 w-full"
            />
          ) : (
            <p>{userInfo.email}</p>
          )}
        </div>

        <div className="profile-field">
          <label className="block font-semibold">Bio:</label>
          {editMode ? (
            <textarea
              name="bio"
              value={updatedInfo.bio}
              onChange={handleInputChange}
              className="border rounded p-2 w-full"
            />
          ) : (
            <p>{userInfo.bio || "No bio available."}</p>
          )}
        </div>
      </div>

      <div className="actions flex gap-2">
        {editMode ? (
          <>
            <button
              onClick={handleSaveChanges}
              className="bg-blue-500 text-white py-2 px-4 rounded"
            >
              Save Changes
            </button>
            <button
              onClick={handleEditToggle}
              className="bg-gray-300 py-2 px-4 rounded"
            >
              Cancel
            </button>
          </>
        ) : (
          <button
            onClick={handleEditToggle}
            className="bg-blue-500 text-white py-2 px-4 rounded"
          >
            Edit Profile
          </button>
        )}

        <button
          onClick={handleLogout}
          className="bg-red-500 text-white py-2 px-4 rounded"
        >
          Logout
        </button>
      </div>
    </div>
  );
}
