import React, { useState, useEffect } from 'react';
import Footer from './Footer';
import Header from './Header';
import { Outlet, useLocation } from 'react-router-dom';

export default function Layout() {
  const [showFooter, setShowFooter] = useState(false);
  const location = useLocation(); // Get current route location

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowFooter(true); 
    }, 3000); // Footer will show after 3 seconds
    return () => clearTimeout(timer);
  }, []);

  // Define routes where the footer should NOT be shown
  const hideFooterRoutes = ['/edit', '/login', '/create'];

  // Check if the current route should hide the footer
  const shouldShowFooter = !hideFooterRoutes.includes(location.pathname);

  return (
    <main className="layout-container">
      <Header />
      <div className="content">
        <Outlet />
      </div>
      {showFooter && shouldShowFooter && <Footer />} {/* Conditionally render footer */}
    </main>
  );
}
