import React from "react";
import './Navbar.css'
import logo_light from '../../image/logo-black.png';
import logo_dark from '../../image/logo-black.png';
import search_icon_light from '../../image/search-w.png';
import search_icon_dark from '../../image/search-b.png';
import toggle_light from '../../image/night.png';
import toggle_dark from '../../image/day.png';
import {Link} from "react-router-dom";
import {useContext, useEffect, } from "react";
import {UserContext} from "./components/UserContext";


export default function Navbar({ theme, setTheme }) {

  const {setUserInfo,userInfo} = useContext(UserContext);
    useEffect(() => {
        fetch('http://localhost:4050/profile', {
        method: 'GET',
        credentials: 'include',
      }).then(response => {
          response.json().then(userInfo => {
              setUserInfo(userInfo);
            });
          });
        }, [setUserInfo]);
        
        function logout() {
            fetch('http://localhost:4050/logout', {
                credentials: 'include',
                method: 'POST',
              });
              setUserInfo(null);
            }
            
            const username = userInfo?.username;
            const role = userInfo?.role;

  const toggle_mode = () => {
    theme === 'light' ? setTheme('dark') : setTheme('light');
      
  };

  
  return (
    <div className="navbar">
      <img src={theme === 'light' ? logo_light : logo_dark} alt="" className="logo1"></img>
      <nav>
          <Link to="/">Home</Link>
          {username && (
          <>
            {role === 'ADMIN' &&
              <Link to="/create">
                Add new course
              </Link>}
            <a href="/" onClick={logout}>Logout ({username})</a>
          </>
        )}
        {!username && (
            <>
              <Link to="/login">Login</Link>
            </>
          )}
        </nav>  
      <div className="search-box">
        <input type="text" placeholder="search" />
        <img src={theme === 'light' ? search_icon_light : search_icon_dark} alt="" />
      </div>

      <img src={theme === 'light' ? toggle_light : toggle_dark} alt="" className="toggle-icon" onClick={() => {toggle_mode()}}/> 
    </div>
  )
}
