import { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { UserContext } from "../components/UserContext";

export default function CommentPage() {
  const { id } = useParams(); // Post ID from the URL
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [hoveredStar, setHoveredStar] = useState(0); // For hover effect on stars
  const [newRating, setNewRating] = useState(0);
  const { userInfo } = useContext(UserContext);

  useEffect(() => {
    // Fetch comments for the specific post
    fetch(`http://localhost:4050/post/${id}/comments`)
      .then((response) => response.json())
      .then((data) => setComments(data));
  }, [id]);

  async function addCommentAndRating() {
    if (!newComment.trim() && newRating === 0) {
      alert("Please provide a comment or rating.");
      return;
    }

    const response = await fetch(`http://localhost:4050/post/${id}/comments`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "include",
      body: JSON.stringify({ text: newComment, rating: newRating }),
    });

    if (response.ok) {
      const addedComment = await response.json();
      setComments([...comments, addedComment]);
      setNewComment("");
      setNewRating(0);
    } else {
      alert("Failed to add comment or rating.");
    }
  }

  async function likeComment(commentId) {
    const response = await fetch(`http://localhost:4050/post/${id}/comments/${commentId}/like`, {
      method: "POST",
      credentials: "include",
    });

    if (response.ok) {
      const updatedComment = await response.json();
      setComments(
        comments.map((comment) => (comment._id === commentId ? updatedComment : comment))
      );
    } else {
      alert("Failed to like the comment.");
    }
  }

  async function editComment(commentId, newText, newRating) {
    const response = await fetch(`http://localhost:4050/post/${id}/comments/${commentId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "include",
      body: JSON.stringify({ text: newText, rating: newRating }),
    });

    if (response.ok) {
      const updatedComment = await response.json();
      setComments(
        comments.map((comment) => (comment._id === commentId ? updatedComment : comment))
      );
    } else {
      alert("Failed to update the comment.");
    }
  }

  return (
    <div className="comment-section">
      <h2>Comments & Ratings</h2>
      <div className="comment-input">
        <textarea
          placeholder="Write a comment..."
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        ></textarea>
        <div className="rating-input">
          <span>Rate: </span>
          {[1, 2, 3, 4, 5].map((star) => (
            <button
              key={star}
              onClick={() => setNewRating(star)}
              onMouseEnter={() => setHoveredStar(star)}
              onMouseLeave={() => setHoveredStar(0)}
              className={star <= (hoveredStar || newRating) ? "active-star" : ""}
            >
              ‚òÖ
            </button>
          ))}
        </div>
        <button onClick={addCommentAndRating}>Submit</button>
      </div>
      <div className="comments-list">
        {comments.map((comment) => (
          <div key={comment._id} className="comment-item">
            <div className="comment-header">
              <strong>@{comment.author.username}</strong>{" "}
              <small>{new Date(comment.createdAt).toLocaleString()}</small>
            </div>
            <p>{comment.text}</p>
            <div className="rating-display">
              <span>Rating: </span>
              {[1, 2, 3, 4, 5].map((star) => (
                <span
                  key={star}
                  className={star <= (comment.rating || 0) ? "active-star" : ""}
                >
                  ‚òÖ
                </span>
              ))}
            </div>
            <div className="comment-actions">
              <button onClick={() => likeComment(comment._id)}>
                üëç {comment.likes || 0}
              </button>
              {userInfo.id === comment.author._id && (
                <button
                  onClick={() =>
                    editComment(
                      comment._id,
                      prompt("Edit your comment:", comment.text),
                      parseInt(prompt("Edit your rating:", comment.rating || 0))
                    )
                  }
                >
                  Edit
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
